  //const result = words.filter(word => word.length > 6);


  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
      <Wrapper>
        <Title>Friends List</Title>

        <Filter
        deparmentFilt={this.deparmentFilt}
